

$(function(){


    function navActiveSet(name){
        var $navlist = $('#accordion .open');

        $navlist.find('.submenu li').removeClass('current');
        $('.open .' +name).addClass('current');

    }
    window.navActiveSet = navActiveSet;

    //绑定用户信息处点击事件
    function bindUserInfoEvent(){
        var $dropdown = $('#dropdown-toggle'),
            $amdin = $('.amdin');

        $dropdown.on('click',function(){
            $('#change-login').toggle();
        })

        $amdin.on('click',function(){
            $('#change-login').toggle();
        })
    }

    //绑定退出登录及切换高校事件
    function bindLoginSwitchAndChangeSchool(){
        $('#change-login li').on('click',loginSwitchAndChangeSchool)
    }

    function loginSwitchAndChangeSchool(){
        if ($(this).attr("op") == 'logout') {
            $.cookie('password', null);
            $.cookie('autoLogin', null);
            window.location.href = '/login/logout';
        } else if ($(this).attr("op") == 'change') {
            window.location.href = '/university/index';
        }
        $('#change-login').hide();
    }

    function UI_set(){
        $(".content").css('height', $(window).height() - 80);
        $(".left-sidebar").css('height', $(window).height() - 80);
        $(".iframe_content").css('max-height', $(window).height() - 80);
        $(".center-box").css('margin-top', ($(window).height() - 233) / 2);
    }
    function windowResize(){
        $(window).on('resize')
    }

    function pageInit(){
        bindUserInfoEvent();
        bindLoginSwitchAndChangeSchool();
        UI_set();
    }

    pageInit();
});