
$(function(){




    $('.btn_del').click(function(){
        var tar = $(this);
        window.Confirm('学期目标删除::none','删除学期目标后将无法恢复,是否删除此学期目标?',function(){delTermGoal(tar)} ,'确认删除::取消');
    });

    //删除学期目标
    function delTermGoal(tar){

        var groupId = tar.attr("data-groupId");
        $.ajax({
            url:'/semester/remove',
            data:{'groupId': groupId},
            method:"POST",
            success:function(data){
                if(data.type == 200){
                    window.location.reload();
                }else {
                    alert(data.msg);

                    window.Alert('学期目标删除::none', data.msg);
                }
            },
            error:function(){
                lock.reset();
            }

        })
    }
    
});